import { useState } from "react";

function ArraysinHooks() {

    const [inputVal, setInputVal] = useState("")
    const [myArray, setArrayValues] = useState(["a", "b", "c", "d"])


    return (
        <>
            <input type="text"
                value={inputVal}
                onChange={e => setInputVal(e.target.value)}
            />
            <button onClick={setArrayValues(myArray.push(inputVal))}> Submit </button>
            <div> The items in the array are </div>
        </>
    )

}

export default ArraysinHooks;
