import { Tag } from "antd";

export const statusDisplay = (rec) => {

    switch (rec) {
        case "UNRELEASED":
            return (
                <div>
                    <Tag color="magenta">{rec}</Tag>
                </div>
            )
            break;
        case "RELEASED":
            return (
                <div>
                    <Tag color="green">{rec}</Tag>
                </div>
            )
        case "IN PROGRESS":
            return (
                <div>
                    <Tag color="blue">{rec}</Tag>
                </div>
            )
    }

}
