    const consoleVal = () => {
        console.log("The value in the arrya", inputVal)
        console.log("The value in the arrya () ", setInputVal)
    }

    return (
        <>
            <input type="text"
                value={inputVal}
                onChange={e => setInputVal({ inputVal: e.target.value })}
            />
            <button onClick={consoleVal}> Submit </button>
            <div> The items in the array are </div>
        </>
    )

}

