import { useState } from "react";
import { render } from "react-dom";



function hooksAsFunction() {

    const [counterValue, incrementCounter] = useState(0)
    // incrementCounter() {
    //     counterValue = counterValue + 1;
    // }



    return (
        <>
            <div> Only here since this is a functional component - we can use hooks </div>>
            <button onClick={() => incrementCounter(counterValue + 1)}> Count + </button>
            <h4> {counterValue} </h4>
        </>
    )


}

export default hooksAsFunction;
