import Link from 'next/link';
import Header from '../components/Header';
import Layout from '../components/MyLayout';
import { Table, Tag } from 'antd';

<link rel="stylesheet" href="../styles/commonStyles.css" />

const PostLink = props => (
    <li>
        <Link href={`/posts?title=${props.title}`}>
            <a>{props.title}</a>
        </Link>
    </li>
)

const columns = [
    {
        title: 'Name',
        dataIndex: 'name',
        key: 'name',
        render: text => <a>{text}</a>,
    },
    {
        title: 'Age',
        dataIndex: 'age',
        key: 'age',
    },
    {
        title: 'Address',
        dataIndex: 'address',
        key: 'address',
    },
    {
        title: 'Tags',
        key: 'tags',
        dataIndex: 'tags',
        render: tags => (
            <span>
                {tags.map(tag => {
                    let color = tag.length > 5 ? 'geekblue' : 'green';
                    if (tag === 'loser') {
                        color = 'volcano';
                    }
                    return (
                        <Tag color={color} key={tag}>
                            {tag.toUpperCase()}
                        </Tag>
                    );
                })}
            </span>
        ),
    },
    {
        title: 'Action',
        key: 'action',
        render: (text, record) => (
            <span>
                <a style={{ marginRight: 16 }}>Invite {record.name}</a>
                <a>Delete</a>
            </span>
        ),
    },
];

const data = [
    {
        key: '1',
        name: 'John Brown',
        age: 32,
        address: 'New York No. 1 Lake Park',
        tags: ['nice', 'developer'],
    },
    {
        key: '2',
        name: 'Jim Green',
        age: 42,
        address: 'London No. 1 Lake Park',
        tags: ['loser'],
    },
    {
        key: '3',
        name: 'Joe Black',
        age: 32,
        address: 'Sidney No. 1 Lake Park',
        tags: ['cool', 'teacher'],
    },
];
export default function Blog() {
    return (
        <>
            <Layout>
                <h1>My Blog</h1>
                <ul>
                    <PostLink title="Hello Next.js page 1" />
                    <PostLink title="Awesome page 2" />
                    <PostLink title="Curfew today page 3" />
                </ul>
            </Layout>
            <Table columns={columns} dataSource={data} />
        </>
    )
}
