import React, { Component, useState } from "react";
import { Table, Tag } from 'antd';
import AddParentRelease from './addParentRelease';





export default class ReleaseList extends Component {

    // const[editingKey, setEditingKey] = useState('');
    constructor(props) {
        super(props);
        this.state = {
            columns: [
                {
                    title: 'Version No',
                    dataIndex: 'version',
                    key: 'version',
                    render: text => <a>{text}</a>,
                },
                {
                    title: 'Status',
                    dataIndex: 'status',
                    key: 'status',
                },
                {
                    title: 'Progress',
                    dataIndex: 'progress',
                    key: 'progress',
                },
                {
                    title: 'Start Date',
                    dataIndex: 'startDate',
                    key: 'startDate',
                },
                {
                    title: 'Release Date',
                    dataIndex: 'endDate',
                    key: 'endDate',
                },
                {
                    title: 'Description',
                    dataIndex: 'description',
                    key: 'description',
                },
                {
                    title: 'Action',
                    key: 'action',
                    render: (text, record) => (
                        <span>
                            <a style={{ marginRight: 16 }} onClick={() => this.edit(record)}>Edit</a>
                            <a>Delete</a>
                        </span>
                    ),
                },
            ],
            listDataSrc: [],
            isEdit: false
        }
    }

    componentDidMount() {
        let chkItem = JSON.parse(localStorage.getItem("parentReleaseData"));
        let getItems = chkItem == null ? [] : chkItem;
        this.setState({
            listDataSrc: getItems
        })
    }

    edit = (record) => {
        console.log("Record", record)
        this.setState({
            isEdit: true,
            columns: [
                {
                    title: 'Version No',
                    dataIndex: 'version',
                    key: 'version',
                    render: (record) => {
                        return (
                            <Form.Item
                                label="version"
                                name="version"
                                rules={[{ required: true, message: 'Please input your version!' }]}
                            >
                                <Input value={record} />
                            </Form.Item>
                        );
                    },
                },
            ]
        })
        // form.setFieldsValue({ ...record });
        // setEditingKey(record.key);
    }

    render() {
        let { columns, listDataSrc } = this.state
        return (
            <>
                <Form >
                    <Table columns={columns} dataSource={listDataSrc} />
                </Form>
                <AddParentRelease />
            </>
        )
    }


}
