import { useState } from "react";
import { render } from "react-dom";



export function hooksAsFunction() {

    const [counterValue, incrementCounter] = useState(0)
    incrementCounter = () => {
        counterValue = counterValue + 1;
    }

    return (
        <>
            <div> Only here since this is a functional component - we can use hooks </div>>
            <Button onClick={incrementCounter} > Count + </Button>
            <h4> {counterValue} </h4>
        </>
    )


}
