const actions = {
    DELETE_ITEM: "DELETE_ITEM",
    DELETE_ITEM_SUCCESS: "DELETE_ITEM_SUCCESS",


    onDeleteItem: () => ({
        type: actions.DELETE_ITEM,
        value: 1
    }),

    onDeleteItemSuccess: (result) => ({
        type: actions.DELETE_ITEM,
        result
    })

}


export default actions;
