import React, { Component, useState, version } from "react";
import { Table, Tag, Form, Input, InputNumber, Popconfirm, DatePicker, Select } from 'antd';
import AddParentRelease from './addParentRelease';
import moment from 'moment';

const dateFormat = "DD-MM-YYYY";
const EditableCell = ({
    editing,
    dataIndex,
    title,
    inputType,
    record,
    index,
    children,
    ...restProps
}) => {
    // console.log("started 1", children)
    console.log("started 2", record)
    // console.log("started 4", dataIndex)
    // console.log("started 5", title)
    let inputNode;
    if (dataIndex == "version") {

    } else if (dataIndex == "startDate") {

    } else {
        inputNode = <Input />
    }

    switch (dataIndex) {
        case "version":
            inputNode = <Input defaultValue={record.version} name="version" />
            break;
        case "startDate":
            inputNode = <DatePicker name="startDate" defaultValue={moment(record.startDate, dateFormat)} />
            break;
        case "endDate":
            inputNode = <DatePicker name="endDate" defaultValue={moment(record.endDate, dateFormat)} />
            break;
        case "description":
            inputNode = <Input defaultValue={record.description} />
            break;
        case "status":
            inputNode =
                <Select style={{ width: 120 }} name="statusSelect">
                    <Option value="inprogress">In Progress</Option>
                    <Option value="unreleased">Unreleased</Option>
                    <Option value="released">Released</Option>
                </Select>
            break;
        case "progress":
            inputNode = <DatePicker defaultValue={moment(record.startDate, dateFormat)} />
            break;
        default:
        // code block
    }

    // const inputNode = inputType === 'number' ? <InputNumber /> : <Input />;
    return (
        <td {...restProps}>
            {editing ?
                (
                    <Form.Item
                        name={dataIndex}
                        style={{ margin: 0 }}
                        rules={[
                            {
                                required: true,
                                message: `Please Input ${title}!`,
                            },
                        ]}
                    >
                        {inputNode}
                    </Form.Item>

                )

                : (
                    children
                )}
        </td>
    );
    // }
};



export default class ReleaseList extends Component {

    constructor(props) {
        super(props);
        this.state = {
            editingKey: '',
            columns: [
                {
                    title: 'Version No',
                    dataIndex: 'version',
                    key: 'version',
                    render: text => <a>{text}</a>,
                    editable: true,
                },
                {
                    title: 'Status',
                    dataIndex: 'status',
                    key: 'status',
                    editable: true,
                },
                {
                    title: 'Progress',
                    dataIndex: 'progress',
                    key: 'progress',
                    editable: true,
                },
                {
                    title: 'Start Date',
                    dataIndex: 'startDate',
                    key: 'startDate',
                    editable: true,
                },
                {
                    title: 'Release Date',
                    dataIndex: 'endDate',
                    key: 'endDate',
                    editable: true,
                },
                {
                    title: 'Description',
                    dataIndex: 'description',
                    key: 'description',
                    editable: true,
                },
                {
                    title: 'Action',
                    key: 'action',
                    render: (text, record) => {
                        const editable = this.isEditing(record);
                        return editable ? (
                            <span>
                                <a
                                    href="javascript:;"
                                    onClick={() => this.save(record.key)}
                                    style={{
                                        marginRight: 8,
                                    }}
                                >
                                    Save
                            </a>
                                <Popconfirm title="Sure to cancel?" onConfirm={this.cancel}>
                                    <a>Cancel</a>
                                </Popconfirm>
                            </span>
                        ) : (
                                <a disabled={this.state.editingKey !== ''} onClick={() => this.edit(record)}>
                                    Edit
                                </a>
                            );
                    }
                }
            ],
            listDataSrc: [],
            isEdit: false
        }
    }

    componentDidMount() {
        let chkItem = JSON.parse(localStorage.getItem("parentReleaseData"));
        let getItems = chkItem == null ? [] : chkItem;
        this.setState({
            listDataSrc: getItems
        })
    }

    isEditing = record => record.key === this.state.editingKey;

    cancel = () => {
        this.setState({ editingKey: '' });
    };

    save(form, key) {
        form.validateFields((error, row) => {
            if (error) {
                return;
            }
            console.log("Form submit", key)
            console.log("Form submit form", form)
            const newData = [...this.state.data];
            const index = newData.findIndex(item => key === item.key);
            if (index > -1) {
                const item = newData[index];
                newData.splice(index, 1, {
                    ...item,
                    ...row,
                });
                this.setState({ data: newData, editingKey: '' });
            } else {
                newData.push(row);
                this.setState({ data: newData, editingKey: '' });
            }
        });
    }

    edit(record) {
        this.setState({ editingKey: record.key });
    }

    render() {
        let { listDataSrc } = this.state;

        const components = {
            body: {
                cell: EditableCell,
            },
        };

        const columns = this.state.columns.map(col => {
            if (!col.editable) {
                return col;
            }
            return {
                ...col,
                onCell: record => ({
                    record,
                    inputType: col.dataIndex,
                    dataIndex: col.dataIndex,
                    title: col.title,
                    editing: this.isEditing(record),
                }),
            };
        });


        return (
            <>
                <Form>
                    <Table columns={columns} dataSource={listDataSrc}
                        components={components}
                        rowClassName="editable-row"
                        pagination={{
                            onChange: this.cancel,
                        }}
                    />
                </Form>

                <AddParentRelease />
            </>
        )
    }


}
