import React, { Component } from "react";
import { Table, Popconfirm, Popover, Row, Col, Button } from "antd";

export default class ChildReleaseList extends Component {


    constructor(props) {
        super(props);
        this.state = {

            editingKey: '',
            childColumns: [

                {
                    title: 'Status',
                    dataIndex: 'status',
                    key: 'status',
                    editable: true,
                },
                {
                    title: 'Progess',
                    dataIndex: 'progress',
                    key: 'progress',
                    editable: true,
                },
                {
                    title: 'Start Date',
                    dataIndex: 'startDate',
                    key: 'startDate',
                    editable: true,
                },
                {
                    title: 'End Date',
                    dataIndex: 'endDate',
                    key: 'endDate',
                    editable: true,
                },
                {
                    title: 'Description',
                    dataIndex: 'description',
                    key: 'description',
                    editable: true,
                },
                {
                    title: 'Action',
                    key: 'action',
                    render: (text, record) => {
                        const editable = this.isEditing(record);
                        const Content = (
                            <div>
                                <Row>
                                    <Col span={8}><a disabled={this.state.editingKey !== ''} onClick={() => this.edit(record)}>Edit</a></Col>
                                    <Col span={8}><a disabled={this.state.editingKey !== ''} onClick={() => this.addChildItem(record)}>Add</a></Col>
                                    <Col span={8}><a disabled={this.state.editingKey !== ''} onClick={() => this.deleteItem(record)}>Delete</a></Col>
                                </Row>
                            </div>
                        );
                        return editable ? (
                            <span>
                                <a
                                    href="javascript:;"
                                    onClick={() => this.save(record)}
                                    style={{
                                        marginRight: 8,
                                    }}
                                >
                                    Save
                            </a>
                                <Popconfirm title="Sure to cancel?" onConfirm={this.cancel}>
                                    <a>Cancel</a>
                                </Popconfirm>
                            </span>
                        ) : (
                                <Popover content={Content} title="Title" trigger="hover">
                                    <Button>Hover me</Button>
                                </Popover>
                            );
                    }
                }
            ],
        }
    }


    isEditing = record => record.key === this.state.editingKey;

    cancel = () => {
        this.setState({ editingKey: '' });
    };

    edit(record) {
        this.setState({ editingKey: record.key });
    }

    render() {

        const components = {
            body: {
                cell: this.EditableCell,
            },
        };

        const columns = this.state.childColumns.map(col => {
            if (!col.editable) {
                return col;
            }
            return {
                ...col,
                onCell: record => ({
                    record,
                    inputType: col.dataIndex,
                    dataIndex: col.dataIndex,
                    title: col.title,
                    editing: this.isEditing(record),
                }),
            };
        });

        return (

            <Table
                columns={this.state.childColumns}
                dataSource={this.props.record.childRelease}
                pagination={false}
                components={components}
            />
        )
    }

}
