import { useState } from "react";

function ArraysinHooks() {

    const [inputVal, setInputVal] = useState({ x: "" })
    const [myArray, setArrayValues] = useState([])


    const addItem = () => {
        setArrayValues([...myArray, {
            id: myArray.length,
            value: Math.floor(Math.random() * 10) + 1
        }
        ])
    }

    return (
        <>
            <hr />
            <div>Hooks demo using array</div>
            <input type="text"
                value={inputVal.x}
                onChange={e => setInputVal({ ...inputVal, x: e.target.value })}
            />
            <button onClick={addItem}> Submit </button>
            <div> The items in the array are {myArray}</div>
            <div>
                <ul>
                    {myArray.map(item => {
                        <li key={item.id}> {item.value}</li>
                    })}
                </ul>
            </div>
        </>
    )

}

export default ArraysinHooks;
