import { useState } from "react";

function ArraysinHooks() {

    const [inputVal, setInputVal] = useState({ x: "" })
    const [myArray, setArrayValues] = useState(["a", "b", "c", "d"])


    const consoleVal = () => {
        console.log("The value in the arrya", inputVal)
        console.log("The value in the arrya () ", myArray)
        let c = inputVal.x;
        myArray.push(c)
        setArrayValues({ myArray })
        console.log("The value in the arrya () final ", myArray)
    }

    return (
        <>
            <hr />
            <div>Hooks demo using array</div>
            <input type="text"
                value={inputVal.x}
                onChange={e => setInputVal({ ...inputVal, x: e.target.value })}

            />
            <button onClick={consoleVal}> Submit </button>
            <div> The items in the array are {myArray}</div>
        </>
    )

}

export default ArraysinHooks;
